package daysix

import (
	"strings"
)

func DaySixPartTwo(inputsring string) int {
	marker := ""
	index := 0
	subString := ""

	for _, v := range inputsring {
		index += 1
		for _, value := range marker {
			subString += string(value)
			if string(v) == string(value) {
				marker = strings.ReplaceAll(marker, subString, "")
				break
			}
		}
		subString = ""
		marker += string(v)
		if len(marker) == 14 {
			return index
		}
	}

	return 0
}

